-- ===== SEED DE LECCIONES PARA TODOS LOS CURSOS =====

-- ========================================
-- CURSO 1: Límites Personales y Asertividad (ID: 1)
-- ========================================

-- Módulo 1: Fundamentos de Límites Personales
INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(1, 1, 1, '¿Qué son los límites personales?', 'Introducción al concepto de límites personales y por qué son fundamentales para tu bienestar', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 900, '<h2>¿Qué son los límites personales?</h2><p>Los límites personales son las líneas invisibles que establecemos entre nosotros y los demás. Definen dónde termina nuestra responsabilidad y comienza la de otros.</p><h3>Tipos de límites:</h3><ul><li><strong>Físicos:</strong> Espacio personal, contacto físico</li><li><strong>Emocionales:</strong> Tus sentimientos y responsabilidad emocional</li><li><strong>Mentales:</strong> Tus pensamientos, valores y opiniones</li><li><strong>Materiales:</strong> Tus posesiones y recursos</li><li><strong>De tiempo:</strong> Cómo usas tu tiempo y energía</li></ul>', 1, 1, 1),

(1, 1, 2, 'Por qué los límites son esenciales', 'Descubre cómo los límites saludables mejoran todas tus relaciones', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 840, '<h2>La importancia de los límites</h2><p>Los límites no son muros que te aíslan, sino puentes que permiten relaciones sanas.</p><h3>Beneficios de límites claros:</h3><ol><li>Mayor autoestima y confianza</li><li>Relaciones más honestas y auténticas</li><li>Menos resentimiento y agotamiento</li><li>Mejor balance vida-trabajo</li><li>Capacidad de decir "no" sin culpa</li></ol>', 2, 1, 1),

(1, 1, 3, 'Identificando tus límites actuales', 'Ejercicios prácticos para reconocer dónde necesitas establecer límites', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1020, '<h2>Evalúa tus límites actuales</h2><p>Antes de establecer nuevos límites, necesitas identificar dónde los necesitas.</p><h3>Señales de límites débiles:</h3><ul><li>Te sientes agotado constantemente</li><li>Dices "sí" cuando quieres decir "no"</li><li>Te sientes resentido con frecuencia</li><li>Asumes responsabilidad por los problemas de otros</li><li>Tienes dificultad para identificar tus necesidades</li></ul>', 3, 0, 1);

-- Módulo 2: Comunicación Asertiva
INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(1, 2, 1, 'Bases de la asertividad', 'Fundamentos de la comunicación asertiva efectiva', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 960, '<h2>Comunicación Asertiva</h2><p>La asertividad es expresar tus necesidades y opiniones de manera clara, directa y respetuosa.</p><h3>Estilos de comunicación:</h3><ul><li><strong>Pasivo:</strong> Evita conflicto, ignora necesidades propias</li><li><strong>Agresivo:</strong> Impone necesidades, ignora a otros</li><li><strong>Asertivo:</strong> Balance entre respeto propio y a otros</li></ul>', 4, 0, 1),

(1, 2, 2, 'Técnicas de comunicación efectiva', 'Herramientas prácticas para comunicarte con claridad', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1080, '<h2>Técnicas asertivas clave</h2><h3>1. La técnica del disco rayado</h3><p>Repite tu mensaje con calma sin alterarte</p><h3>2. El banco de niebla</h3><p>Acepta parte de la crítica sin ceder terreno</p><h3>3. Aplazamiento asertivo</h3><p>Pide tiempo para pensar antes de responder</p><h3>4. El lenguaje YO</h3><p>"Yo siento..." en lugar de "Tú me haces..."</p>', 5, 0, 1),

(1, 2, 3, 'Practicando la asertividad', 'Escenarios reales y cómo manejarlos', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1140, '<h2>Práctica en situaciones reales</h2><p>Aprende a aplicar la asertividad en tu vida diaria.</p><h3>Escenarios comunes:</h3><ol><li>Decir no a un favor</li><li>Pedir lo que necesitas</li><li>Expresar desacuerdo</li><li>Establecer un límite</li><li>Manejar críticas</li></ol>', 6, 0, 1);

-- ========================================
-- CURSO 2: Superando el Miedo al Rechazo (ID: 2)
-- ========================================

-- Módulo 1: Entendiendo el Miedo al Rechazo
INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(2, 1, 1, 'Orígenes del miedo al rechazo', 'Comprende las raíces de tu miedo', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1200, '<h2>¿De dónde viene el miedo al rechazo?</h2><p>El miedo al rechazo es evolutivo pero puede volverse limitante.</p><h3>Orígenes comunes:</h3><ul><li>Experiencias de infancia</li><li>Críticas tempranas</li><li>Apego inseguro</li><li>Traumas relacionales</li><li>Mensajes culturales</li></ul>', 1, 1, 1),

(2, 1, 2, 'Cómo afecta tu vida actual', 'Identifica el impacto del miedo en tu día a día', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1020, '<h2>El costo del miedo al rechazo</h2><p>Este miedo puede limitar todas las áreas de tu vida.</p><h3>Áreas afectadas:</h3><ul><li>Relaciones personales</li><li>Carrera profesional</li><li>Creatividad y expresión</li><li>Toma de decisiones</li><li>Bienestar emocional</li></ul>', 2, 0, 1),

(2, 1, 3, 'Patrones de evitación', 'Reconoce tus estrategias de evitación', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 960, '<h2>Cómo evitamos el rechazo</h2><p>La evitación nos protege temporalmente pero nos limita a largo plazo.</p><h3>Patrones comunes:</h3><ul><li>Perfeccionismo</li><li>Complacencia excesiva</li><li>Aislamiento social</li><li>Procrastinación</li><li>Auto-sabotaje</li></ul>', 3, 0, 1);

-- Módulo 2: Construyendo Autoestima Sólida
INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(2, 2, 1, 'Bases de la autoestima', 'Fundamentos de una autoestima saludable', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1080, '<h2>¿Qué es la autoestima real?</h2><p>La autoestima no depende de logros externos sino de aceptación interna.</p><h3>Pilares de la autoestima:</h3><ul><li>Auto-conocimiento</li><li>Auto-aceptación</li><li>Auto-respeto</li><li>Auto-confianza</li></ul>', 4, 0, 1),

(2, 2, 2, 'Autocompasión práctica', 'Aprende a tratarte con amabilidad', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1140, '<h2>El poder de la autocompasión</h2><p>Tratarte con la misma amabilidad que tratarías a un amigo querido.</p><h3>Práctica de autocompasión:</h3><ol><li>Reconoce tu sufrimiento</li><li>Normaliza la experiencia humana</li><li>Responde con amabilidad</li></ol>', 5, 0, 1),

(2, 2, 3, 'Tu valor inherente', 'Descubre tu valía más allá de logros', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1020, '<h2>Valor incondicional</h2><p>Tu valor como persona no depende de lo que haces o logras.</p><h3>Separando valor de rendimiento:</h3><ul><li>Eres valioso por existir</li><li>Los errores no definen tu valor</li><li>El rechazo no disminuye tu valía</li></ul>', 6, 0, 1);

-- ========================================
-- CURSO 3: Gestión de Conflictos (ID: 3)
-- ========================================

INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(3, 1, 1, 'Tipos de conflictos', 'Comprende la naturaleza de los conflictos', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 900, '<h2>Anatomía del conflicto</h2><p>No todos los conflictos son iguales.</p><h3>Tipos principales:</h3><ul><li>De intereses</li><li>De valores</li><li>De necesidades</li><li>De información</li><li>Estructurales</li></ul>', 1, 1, 1),

(3, 1, 2, 'Estilos de manejo', 'Identifica tu estilo de manejo de conflictos', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 960, '<h2>5 estilos de manejo de conflictos</h2><ol><li>Competir</li><li>Evitar</li><li>Acomodar</li><li>Comprometer</li><li>Colaborar</li></ol>', 2, 0, 1),

(3, 2, 1, 'Escucha activa avanzada', 'Domina la escucha empática', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1080, '<h2>El arte de escuchar</h2><p>La mayoría de conflictos se resuelven cuando ambas partes se sienten escuchadas.</p>', 3, 0, 1),

(3, 2, 2, 'Comunicación no violenta', 'Técnica CNV de Marshall Rosenberg', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1200, '<h2>Los 4 pasos de la CNV</h2><ol><li>Observación</li><li>Sentimiento</li><li>Necesidad</li><li>Petición</li></ol>', 4, 0, 1);

-- ========================================
-- CURSO 4: Inteligencia Emocional (ID: 4)
-- ========================================

INSERT OR IGNORE INTO lessons (course_id, module_number, lesson_number, title, description, video_url, video_duration, content, order_index, is_preview, published) VALUES
(4, 1, 1, '¿Qué es la inteligencia emocional?', 'Introducción a la IE y sus componentes', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1020, '<h2>Inteligencia Emocional</h2><p>La capacidad de reconocer, comprender y gestionar emociones.</p><h3>5 componentes:</h3><ol><li>Auto-conciencia</li><li>Auto-regulación</li><li>Motivación</li><li>Empatía</li><li>Habilidades sociales</li></ol>', 1, 1, 1),

(4, 1, 2, 'Los 5 componentes clave', 'Profundiza en cada componente de la IE', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1140, '<h2>Componentes en detalle</h2><p>Cada componente es una habilidad entrenable.</p>', 2, 0, 1),

(4, 2, 1, 'Reconociendo emociones', 'Desarrolla vocabulario emocional', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 960, '<h2>La rueda de emociones</h2><p>Más allá de "feliz", "triste", "enojado".</p>', 3, 0, 1),

(4, 3, 1, 'Gestión del estrés', 'Técnicas de regulación emocional', 'https://www.youtube.com/embed/dQw4w9WgXcQ', 1200, '<h2>Herramientas anti-estrés</h2><ul><li>Respiración</li><li>Mindfulness</li><li>Ejercicio</li><li>Conexión social</li></ul>', 4, 0, 1);
